<?php

class WpProQuiz_View_View
{

    private $data = array();

    public function __set($name, $value)
    {
        $this->data[$name] = $value;
    }

    public function __get($name)
    {
        if (isset($this->data[$name])) {
            return $this->data[$name];
        }
    }

    public static function admin_notices($msg, $type = 'error')
    {
        if ($type === 'info') {
            echo '<div class="updated"><p><strong>' . $msg . '</strong></p></div>';
        } else {
            echo '<div class="error"><p><strong>' . $msg . '</strong></p></div>';
        }
    }

    public function redirect($url)
    {

    }

    public function checked($v, $check = true, $echo = true)
    {

        $r = ($v == $check) ? 'checked="checked"' : '';

        if ($echo) {
            echo $r;
        } else {
            return $r;
        }
    }

    public function selected($v, $check = true, $echo = true)
    {
        $r = ($v == $check) ? 'selected="selected"' : '';

        if ($echo) {
            echo $r;
        } else {
            return $r;
        }
    }

    public function selectedArray($v, $check)
    {
        $a = array();

        foreach ($check as $c) {
            $a[] = ($v == $c) ? 'selected="selected"' : '';
        }

        return $a;
    }

    public function isDisplayNone($v)
    {
        echo $v ? '' : 'style="display:none;"';
    }

    /**
     * Check whether current quiz type is photocontest
     * @return bool
     */
    public function checkPhotoOrGiveAwayQuiz($checkPC = false)
    {
        if($checkPC){
            //if only wants to check photocontest status
            return ($this->quiz->getCategoryValue() == PHOTO_CONTEST_VALUE);
        }
        return ($this->quiz->getCategoryValue() == PHOTO_CONTEST_VALUE || $this->quiz->getCategoryValue() == GIVEAWAY_CONTEST_VALUE);
    }

    /**
     * Check whether current quiz type is this or that
     * @return bool
     */
    public function checkThisThatQuiz()
    {
        return ($this->quiz->getCategoryValue() == THIS_OR_THAT_QUIZ_VALUE);
    }

    /**
     * Display loaderbox
     * @return bool
     */
    public function displayLoader()
    {

    ?>
        <div id="wpProQuiz_loadDataHistory" class="wpProQuiz_blueBox"
             style="background-color: #F8F5A8; display: none;">
            <img alt="load"
                 src="data:image/gif;base64,">
            <?php _e('Loading', 'wp-pro-quiz'); ?>
        </div>
    <?php
    }
}
